#!/usr/bin/env python3\n\nimport argparse\nimport subprocess\nimport sys\n\n\ndef run_command(command, dry_run=False):\n    """Run a shell command and print its output."""\n    if dry_run:\n        print(f"[DRY RUN] Would execute: {command}")\n        return True\n    \n    print(f"Executing: {command}")\n    process = subprocess.Popen(command, shell=True, stdout=subprocess.PIPE, stderr=subprocess.PIPE)\n    stdout, stderr = process.communicate()\n    \n    if process.returncode != 0:\n        print(f"Error executing command: {command}\n{stderr.decode('utf-8')}")\n        return False\n    \n    print(stdout.decode('utf-8'))\n    return True\n\n\ndef main(repo, dry_run):\n    """Main function to run the lint-fix skill."""\n    steps = [\n        {"command": "command -v eslint", "name": "Check if ESLint is available", "on_fail": "stop"},\n        {"command": "eslint . --quiet", "name": "Run ESLint in diagnostic mode", "on_fail": "continue"},\n        {"command": "eslint . --fix", "name": "Apply ESLint auto-fixes", "on_fail": "stop", "requires_confirmation": True},\n        {"command": "eslint . --quiet || echo 'Linting complete with remaining issues'", "name": "Summarize linting results", "on_fail": "continue"}\n    ]\n    \n    for step in steps:\n        print(f"Step: {step['name']}")\n        if step.get("requires_confirmation", False):\n            confirm = input("Do you want to proceed with this step? (y/n): ").strip().lower()\n            if confirm != 'y':\n                print("Step skipped by user.")\n                if step["on_fail"] == "stop":\n                    print("Stopping due to user confirmation.")\n                    sys.exit(1)\n                continue\n        \n        success = run_command(step["command"], dry_run)\n        if not success and step["on_fail"] == "stop":\n            print(f"Step failed: {step['name']}")\n            sys.exit(1)\n    \n    print("Lint-fix skill completed successfully.")\n\nif __name__ == "__main__":\n    parser = argparse.ArgumentParser(description='Run the lint-fix skill.')\n    parser.add_argument('--repo', type=str, help='Path to the repository')\n    parser.add_argument('--dry-run', action='store_true', help='Run in dry-run mode')\n    \n    args = parser.parse_args()\n    main(args.repo, args.dry_run)