#!/usr/bin/env python3\n\nimport os\nimport subprocess\nimport sys\n\n\ndef test_script_exists():\n    """Test if the run.py script exists."""\n    script_path = os.path.join(os.path.dirname(__file__), '../scripts/run.py')\n    assert os.path.exists(script_path), "run.py script does not exist"\n    print("test_script_exists: PASSED")\n\n\ndef test_dry_run():\n    """Test the script with the --dry-run flag."""\n    script_path = os.path.join(os.path.dirname(__file__), '../scripts/run.py')\n    result = subprocess.run([sys.executable, script_path, '--dry-run'], capture_output=True, text=True)\n    assert result.returncode == 0, "Dry run failed"\n    print("test_dry_run: PASSED")\n\nif __name__ == "__main__":\n    test_script_exists()\n    test_dry_run()